mapper.select:
SELECT Count(tb_store.id) AS storeVoCount, tb_classify.classify_name AS classifyName, (SELECT COUNT(tb_store.id) FROM tb_store WHERE tb_store.store_status = 1 OR tb_store.store_status = 2 OR tb_store.store_status = 3
) AS sumStorelength FROM tb_store inner JOIN tb_classify ON tb_store.store_classify = tb_classify.id WHERE tb_store.store_status = 1 OR tb_store.store_status = 2 OR tb_store.store_status = 3 GROUP BY tb_classify.id
